$(document).ready(function(){$.request("onCountdownDate",{success:function(a){$("#countdown").countdown(a.date).on("update.countdown",function(a){$(this).html(a.strftime("<span>%-w</span> %!w:week,weeks; <span>%-d</span> %!d:day,days; <span>%H</span> hours <span>%M</span> min <span>%S</span> sec"));$("#countdown").addClass("show")})}})});